import { Component, OnInit } from '@angular/core';
import { ItemService } from '../item.service';

@Component({
  selector: 'app-item',
  templateUrl: './item.component.html',
  styleUrls: ['./item.component.css'],
})
export class ItemComponent implements OnInit {
  items: any[] = [];
  currentItem: any = {};
  successMessage: string = '';
  deleteItemId: string = '';
  searchTerm: string = '';

  constructor(private itemService: ItemService) {}

  ngOnInit(): void {
    this.getItems();
  }

  getItems(): void {
    this.itemService.getItems().subscribe((items) => {
      this.items = items;
    });
  }

  getItemById(id: string): void {
    this.itemService.getItemById(id).subscribe((item) => {
      this.currentItem = item;
    });
  }

  createItem(item: any): void {
    this.itemService.createItem(item).subscribe(() => {
      this.getItems();
      this.currentItem = {};
    });
  }

  updateItem(id: string, item: any): void {
    this.itemService.updateItem(id, item).subscribe(() => {
      this.getItems();
      this.currentItem = {};
    });
  }

  deleteItem(id: string): void {
    this.itemService.deleteItem(id).subscribe(() => {
      this.getItems();
    });
  }

  editItem(id: string): void {
    this.getItemById(id);
  }
  confirmDelete(itemId: string): void {
    this.deleteItemId = itemId;
    // Mostrar modal de confirmación o alerta
  }

  deleteConfirmed(): void {
    // Lógica para eliminar un item después de confirmación
    this.items = this.items.filter((item) => item._id !== this.deleteItemId);
    this.deleteItemId = '';
    this.successMessage = 'Item deleted successfully.';
  }
  // Filtrar y ordenar la lista
  get filteredItems(): any[] {
    return this.items
      .filter(
        (item) =>
          item.name.toLowerCase().includes(this.searchTerm.toLowerCase()) ||
          item.description.toLowerCase().includes(this.searchTerm.toLowerCase())
      )
      .sort((a, b) => a.name.localeCompare(b.name));
  }
}
<div class="container mt-4">
  <h2>Películas</h2>

  <!-- Mensaje de éxito -->
  <div *ngIf="successMessage" class="alert alert-success">
    {{ successMessage }}
  </div>

  <!-- Formulario de creación/actualización de películas -->
  <form
    (ngSubmit)="
      currentItem._id
        ? updateItem(currentItem._id, currentItem)
        : createItem(currentItem)
    "
  >
    <div class="form-group">
      <label for="title">Título:</label>
      <input
        type="text"
        class="form-control"
        [(ngModel)]="currentItem.title"
        name="titulo"
        required
      />
    </div>

    <div class="form-group">
      <label for="description">Descripción:</label>
      <textarea
        class="form-control"
        [(ngModel)]="currentItem.description"
        name="descripcion"
        required
      ></textarea>
    </div>

    <div class="form-group">
      <label for="genre">Género:</label>
      <input
        type="text"
        class="form-control"
        [(ngModel)]="currentItem.genre"
        name="genre"
      />
    </div>

    <div class="form-group">
      <label for="releaseDate">Fecha de Estreno:</label>
      <input
        type="date"
        class="form-control"
        [(ngModel)]="currentItem.releaseDate"
        name="releaseDate"
      />
    </div>

    <!-- Agrega más campos según las necesidades de tu aplicación -->

    <button type="submit" class="btn btn-primary">
      {{ currentItem._id ? "Actualizar" : "Agregar" }}
    </button>
  </form>

  <hr />

  <!-- Campo de búsqueda -->
  <input
    type="text"
    class="form-control mb-3"
    placeholder="Buscar películas"
    [(ngModel)]="searchTerm"
  />

  <!-- Lista de películas -->
  <table class="table">
    <thead>
      <tr>
        <th>Título</th>
        <th>Descripción</th>
        <th>Género</th>
        <th>Fecha de Estreno</th>
        <th>Acciones</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let item of filteredItems">
        <td>{{ item.titulo }}</td>
        <td>{{ item.descripcion }}</td>
        <td>{{ item.genero }}</td>
        <td>{{ item.fecha | date }}</td>
        <td>
          <!-- Botones de acción con confirmación para eliminar -->
          <button class="btn btn-sm btn-primary" (click)="editItem(item._id)">
            Editar
          </button>
          <button
            class="btn btn-sm btn-danger"
            (click)="confirmDelete(item._id)"
          >
            Eliminar
          </button>
        </td>
      </tr>
    </tbody>
  </table>
</div>
