import { Component, OnInit } from '@angular/core';
import { ItemService } from '../item.service';

@Component({
  selector: 'app-item',
  templateUrl: './item.component.html',
  styleUrls: ['./item.component.css'],
})
export class ItemComponent implements OnInit {
  items: any[] = [];
  currentItem: any = {};
  successMessage: string = '';
  deleteItemId: string = '';
  searchTerm: string = '';

  constructor(private itemService: ItemService) {}

  ngOnInit(): void {
    this.getItems();
  }

  getItems(): void {
    this.itemService.getItems().subscribe((items) => {
      this.items = items;
    });
  }

  getItemById(id: string): void {
    this.itemService.getItemById(id).subscribe((item) => {
      this.currentItem = item;
    });
  }

  createItem(item: any): void {
    this.itemService.createItem(item).subscribe(
      () => {
        this.getItems();
        this.currentItem = {};
        this.closeModal();
        this.successMessage = 'Pelicula creada exitosamente.';
        setTimeout(() => {
          this.successMessage = '';
        }, 3000);
      },
      (error) => {
        console.error('Error al agregar película:', error);
      }
    );
  }

  updateItem(id: string, item: any): void {
    this.itemService.updateItem(id, item).subscribe(
      () => {
        this.getItems();
        this.currentItem = {};
        this.closeModal();
        this.successMessage = 'Pelicula actualizada correctamente.';
        setTimeout(() => {
          this.successMessage = '';
        }, 3000);
      },
      (error) => {
        console.error('Error al actualizar película:', error);
      }
    );
  }

  deleteItem(id: string): void {
    this.itemService.deleteItem(id).subscribe(() => {
      this.getItems();
      this.successMessage = 'Pelicula eliminada correctamente.';
      setTimeout(() => {
        this.successMessage = '';
      }, 3000);
    });
  }

  editItem(id: string): void {
    this.getItemById(id);
    this.openModal();
  }

  deleteConfirmed(): void {
    // Lógica para eliminar un item después de confirmación
    this.items = this.items.filter((item) => item._id !== this.deleteItemId);
    this.deleteItemId = '';
  }
  // Filtrar y ordenar la lista
  get filteredItems(): any[] {
    return this.items
      .filter(
        (item) =>
          item.name.toLowerCase().includes(this.searchTerm.toLowerCase()) ||
          item.description.toLowerCase().includes(this.searchTerm.toLowerCase())
      )
      .sort((a, b) => a.name.localeCompare(b.description));
  }
  showModal: boolean = false;
  modalTitle: string = 'Agregar Película';

  // Función para abrir el modal
  openModal() {
    this.showModal = true;
    this.modalTitle = this.currentItem._id
      ? 'Editar Película'
      : 'Agregar Película';
  }

  // Función para cerrar el modal
  closeModal() {
    this.showModal = false;
    this.modalTitle = 'Agregar Película';
  }
  confirmDelete(item: any): void {
    const isConfirmed = window.confirm(
      `¿Estás seguro de que deseas eliminar la película "${item.name}"?`
    );

    if (isConfirmed) {
      // Realizar la lógica para eliminar el elemento
      this.deleteItem(item._id);
    }
  }
  deleteConfirmationItem: any;
  showDeleteConfirmationModal: boolean = false;

  openDeleteConfirmationModal(item: any): void {
    this.deleteConfirmationItem = item;
    this.showDeleteConfirmationModal = true;
  }

  closeDeleteConfirmationModal(): void {
    this.deleteConfirmationItem = null;
    this.showDeleteConfirmationModal = false;
  }
}
