import { Component, OnInit } from '@angular/core';
import { ItemService } from '../item.service';

@Component({
  selector: 'app-item',
  templateUrl: './item.component.html',
  styleUrls: ['./item.component.css'],
})
export class ItemComponent implements OnInit {
  items: any[] = [];
  currentItem: any = {};

  constructor(private itemService: ItemService) {}

  ngOnInit(): void {
    this.getItems();
  }

  getItems(): void {
    this.itemService.getItems().subscribe((items) => {
      this.items = items;
    });
  }

  getItemById(id: string): void {
    this.itemService.getItemById(id).subscribe((item) => {
      this.currentItem = item;
    });
  }

  createItem(item: any): void {
    this.itemService.createItem(item).subscribe(() => {
      this.getItems();
      this.currentItem = {};
    });
  }

  updateItem(id: string, item: any): void {
    this.itemService.updateItem(id, item).subscribe(() => {
      this.getItems();
      this.currentItem = {};
    });
  }

  deleteItem(id: string): void {
    this.itemService.deleteItem(id).subscribe(() => {
      this.getItems();
    });
  }

  editItem(id: string): void {
    this.getItemById(id);
  }
}
<!-- app.component.html -->

<div class="container mt-4">
  <h2>Items</h2>

  <form
    (ngSubmit)="
      form.valid &&
        (currentItem._id
          ? updateItem(currentItem._id, currentItem)
          : createItem(currentItem))
    "
    #form="ngForm"
  >
    <div class="form-group">
      <label for="name">Nombre de pelicula:</label>
      <input
        type="text"
        class="form-control"
        [(ngModel)]="currentItem.name"
        name="name"
        required
        #name="ngModel"
        [ngClass]="{
          'is-invalid': name.invalid && (name.dirty || name.touched)
        }"
      />
      <div
        class="invalid-feedback"
        *ngIf="name.invalid && (name.dirty || name.touched)"
      >
        Name is required.
      </div>
    </div>

    <div class="form-group">
      <label for="description">Description:</label>
      <input
        type="text"
        class="form-control"
        [(ngModel)]="currentItem.description"
        name="description"
        required
        #description="ngModel"
        [ngClass]="{
          'is-invalid':
            description.invalid && (description.dirty || description.touched)
        }"
      />
      <div
        class="invalid-feedback"
        *ngIf="
          description.invalid && (description.dirty || description.touched)
        "
      >
        Description is required.
      </div>
    </div>

    <button type="submit" class="btn btn-primary">
      {{ currentItem._id ? "Update" : "Add" }}
    </button>
  </form>

  <hr />

  <!-- Lista de items -->
  <table class="table">
    <thead>
      <tr>
        <th>Name</th>
        <th>Description</th>
        <th>Actions</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let item of items">
        <td>{{ item.name }}</td>
        <td>{{ item.description }}</td>
        <td>
          <button class="btn btn-sm btn-primary" (click)="editItem(item._id)">
            Edit
          </button>
          <button class="btn btn-sm btn-danger" (click)="deleteItem(item._id)">
            Delete
          </button>
        </td>
      </tr>
    </tbody>
  </table>
</div>
