import { Component, OnInit } from '@angular/core';
import { ItemService } from '../item.service';

@Component({
  selector: 'app-item',
  templateUrl: './item.component.html',
  styleUrls: ['./item.component.css'],
})
export class ItemComponent implements OnInit {
  items: any[] = [];
  currentItem: any = {};
  successMessage: string = '';
  deleteItemId: string = '';
  searchTerm: string = '';

  constructor(private itemService: ItemService) {}

  ngOnInit(): void {
    this.getItems();
  }

  getItems(): void {
    this.itemService.getItems().subscribe((items) => {
      this.items = items;
    });
  }

  getItemById(id: string): void {
    this.itemService.getItemById(id).subscribe((item) => {
      this.currentItem = item;
    });
  }

  createItem(item: any): void {
    this.itemService.createItem(item).subscribe(() => {
      this.getItems();
      this.currentItem = {};
    });
  }

  updateItem(id: string, item: any): void {
    this.itemService.updateItem(id, item).subscribe(() => {
      this.getItems();
      this.currentItem = {};
    });
  }

  deleteItem(id: string): void {
    this.itemService.deleteItem(id).subscribe(() => {
      this.getItems();
    });
  }

  editItem(id: string): void {
    this.getItemById(id);
  }
  confirmDelete(itemId: string): void {
    this.deleteItemId = itemId;
  }

  deleteConfirmed(): void {
    // Lógica para eliminar un item después de confirmación
    this.items = this.items.filter((item) => item._id !== this.deleteItemId);
    this.deleteItemId = '';
    this.successMessage = 'Item deleted successfully.';
  }
  // Filtrar y ordenar la lista
  get filteredItems(): any[] {
    return this.items
      .filter(
        (item) =>
          item.name.toLowerCase().includes(this.searchTerm.toLowerCase()) ||
          item.description.toLowerCase().includes(this.searchTerm.toLowerCase())
      )
      .sort((a, b) => a.name.localeCompare(b.description));
  }
}
<div class="container mt-4">
  <h2>Películas</h2>

  <div *ngIf="successMessage" class="alert alert-success">
    {{ successMessage }}
  </div>
  <!-- Button trigger modal -->
  <button
    type="button"
    class="btn btn-primary"
    data-bs-toggle="modal"
    data-bs-target="#exampleModal"
  >
    Launch demo modal
  </button>

  <!-- Modal -->
  <div
    class="modal fade"
    id="exampleModal"
    tabindex="-1"
    aria-labelledby="exampleModalLabel"
    aria-hidden="true"
  >
    <div class="modal-dialog">
      <div class="modal-content">
        <div class="modal-header">
          <h1 class="modal-title fs-5" id="exampleModalLabel">Modal title</h1>
          <button
            type="button"
            class="btn-close"
            data-bs-dismiss="modal"
            aria-label="Close"
          ></button>
        </div>
        <div class="modal-body">...</div>
        <div class="modal-footer">
          <button
            type="button"
            class="btn btn-secondary"
            data-bs-dismiss="modal"
          >
            Close
          </button>
          <button type="button" class="btn btn-primary">Save changes</button>
        </div>
      </div>
    </div>
  </div>
  <form
    (ngSubmit)="
      currentItem._id
        ? updateItem(currentItem._id, currentItem)
        : createItem(currentItem)
    "
  >
    <div class="row">
      <div class="form-group col-6">
        <label for="name">Título:</label>
        <input
          type="text"
          class="form-control"
          [(ngModel)]="currentItem.name"
          name="name"
          required
          #name="ngModel"
          [ngClass]="{
            'is-invalid': name.invalid && (name.dirty || name.touched)
          }"
        />
        <div
          class="invalid-feedback"
          *ngIf="name.invalid && (name.dirty || name.touched)"
        >
          Este campo es requerido.
        </div>
      </div>

      <div class="form-group col-6">
        <label for="description">Descripción:</label>
        <textarea
          class="form-control"
          [(ngModel)]="currentItem.description"
          name="description"
          required
          #description="ngModel"
          [ngClass]="{
            'is-invalid':
              description.invalid && (description.dirty || description.touched)
          }"
        ></textarea>
        <div
          class="invalid-feedback"
          *ngIf="name.invalid && (name.dirty || name.touched)"
        >
          Este campo es requerido.
        </div>
      </div>
      <div class="form-group col-6">
        <label for="genero">Género:</label>
        <input
          type="text"
          class="form-control"
          [(ngModel)]="currentItem.genero"
          name="genero"
        />
      </div>

      <div class="form-group col-6">
        <label for="fecha">Fecha de Estreno:</label>
        <input
          type="date"
          class="form-control"
          [(ngModel)]="currentItem.fecha"
          name="fecha"
        />
      </div>
      <div class="form-group col-6">
        <label for="imagen">Url de la imagen:</label>
        <input
          class="form-control"
          [(ngModel)]="currentItem.imagen"
          name="imagen"
        />
      </div>
    </div>

    <button type="submit" class="btn btn-info text-white col-6">
      {{ currentItem._id ? "Actualizar" : "Agregar" }}
    </button>
  </form>

  <hr />

  <input
    type="text"
    class="form-control mb-3"
    placeholder="Buscar películas"
    [(ngModel)]="searchTerm"
  />

  <table class="table">
    <thead>
      <tr>
        <th>Título</th>
        <th>Descripción</th>
        <th>Género</th>
        <th>Fecha de Estreno</th>
        <th>Imagen</th>
        <th>Acciones</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let item of filteredItems">
        <td>{{ item.name }}</td>
        <td>{{ item.description }}</td>
        <td>{{ item.genero }}</td>
        <td>{{ item.fecha | date }}</td>
        <td>{{ item.imagen }}</td>
        <td>
          <!-- Botones de acción con confirmación para eliminar -->
          <button class="btn btn-sm btn-primary" (click)="editItem(item._id)">
            Editar
          </button>
          <button
            class="btn btn-sm btn-danger"
            (click)="confirmDelete(item._id)"
          >
            Eliminar
          </button>
        </td>
      </tr>
    </tbody>
  </table>
</div>
