import { Component, OnInit } from '@angular/core';
import { ItemService } from '../item.service';
import { NgForm } from '@angular/forms';
@Component({
  selector: 'app-item',
  templateUrl: './item.component.html',
  styleUrls: ['./item.component.css'],
})
export class ItemComponent implements OnInit {
  items: any[] = [];
  currentItem: any = {};
  successMessage: string = '';
  deleteItemId: string = '';
  searchTerm: string = '';

  constructor(private itemService: ItemService) {}

  ngOnInit(): void {
    this.getItems();
  }

  getItems(): void {
    this.itemService.getItems().subscribe((items) => {
      this.items = items;
    });
  }

  getItemById(id: string): void {
    this.itemService.getItemById(id).subscribe((item) => {
      this.currentItem = item;
    });
  }

  createItem(item: any): void {
    this.itemService.createItem(item).subscribe(() => {
      this.getItems();
      this.currentItem = {};
      this.closeModal();
      this.successMessage = 'Pelicula creada exitosamente.';
      setTimeout(() => {
        this.successMessage = '';
      }, 3000);
    });
  }

  updateItem(id: string, item: any): void {
    this.itemService.updateItem(id, item).subscribe(() => {
      this.getItems();
      this.currentItem = {};
      this.closeModal();
      this.successMessage = 'Pelicula actualizada correctamente.';
      setTimeout(() => {
        this.successMessage = '';
      }, 3000);
    });
  }

  deleteItem(id: string): void {
    this.itemService.deleteItem(id).subscribe(() => {
      this.getItems();
      this.successMessage = 'Pelicula eliminada correctamente.';
      setTimeout(() => {
        this.successMessage = '';
      }, 3000);
    });
  }

  editItem(id: string): void {
    this.getItemById(id);
    this.openModal();
  }
  confirmDelete(itemId: string): void {
    this.deleteItemId = itemId;
  }

  deleteConfirmed(): void {
    // Lógica para eliminar un item después de confirmación
    this.items = this.items.filter((item) => item._id !== this.deleteItemId);
    this.deleteItemId = '';
  }
  // Filtrar y ordenar la lista
  get filteredItems(): any[] {
    return this.items
      .filter(
        (item) =>
          item.name.toLowerCase().includes(this.searchTerm.toLowerCase()) ||
          item.description.toLowerCase().includes(this.searchTerm.toLowerCase())
      )
      .sort((a, b) => a.name.localeCompare(b.description));
  }
  showModal: boolean = false;
  modalTitle: string = 'Agregar Película';

  // Función para abrir el modal
  openModal() {
    this.showModal = true;
    this.modalTitle = this.currentItem._id
      ? 'Editar Película'
      : 'Agregar Película';
  }

  // Función para cerrar el modal
  closeModal(form: NgForm) {
    // Solo cerrar el modal si el formulario es válido
    if (form.valid) {
      this.showModal = false;
      this.modalTitle = 'Agregar Película';
    }
  }
}
<div class="container mt-4">
  <h2>Películas</h2>

  <div *ngIf="successMessage" class="alert alert-success">
    {{ successMessage }}
  </div>

  <!-- Button trigger modal -->
  <button
    type="button"
    class="btn btn-info text-white col-4"
    (click)="openModal()"
  >
    AGREGAR PELICULA
  </button>
  <div
    class="modal"
    [style.display]="showModal ? 'block' : 'none'"
    tabindex="-1"
    role="dialog"
  >
    <div class="modal-dialog" role="document">
      <div class="modal-content">
        <div class="modal-header">
          <h1 class="modal-title fs-5">{{ modalTitle }}</h1>
          <button
            type="button"
            class="btn-close"
            (click)="closeModal()"
            aria-label="Cancelar"
          ></button>
        </div>
        <form (ngSubmit)="onSubmit(itemForm)" #itemForm="ngForm">
          <div class="modal-body row">
            <div class="form-group col-6">
              <label for="name">Título:</label>
              <input
                type="text"
                class="form-control"
                [(ngModel)]="currentItem.name"
                name="name"
                required
                #name="ngModel"
                [ngClass]="{
                  'is-invalid': name.invalid && (name.dirty || name.touched)
                }"
              />
              <div
                class="invalid-feedback"
                *ngIf="name.invalid && (name.dirty || name.touched)"
              >
                Este campo es requerido.
              </div>
            </div>

            <div class="form-group col-6">
              <label for="description">Descripción:</label>
              <textarea
                class="form-control"
                [(ngModel)]="currentItem.description"
                name="description"
                required
                #description="ngModel"
                [ngClass]="{
                  'is-invalid':
                    description.invalid &&
                    (description.dirty || description.touched)
                }"
              ></textarea>
              <div
                class="invalid-feedback"
                *ngIf="name.invalid && (name.dirty || name.touched)"
              >
                Este campo es requerido.
              </div>
            </div>
            <div class="form-group col-6">
              <label for="genero">Género:</label>
              <input
                type="text"
                class="form-control"
                [(ngModel)]="currentItem.genero"
                name="genero"
                required
                #genero="ngModel"
                [ngClass]="{
                  'is-invalid':
                    genero.invalid && (genero.dirty || genero.touched)
                }"
              />
              <div
                class="invalid-feedback"
                *ngIf="genero.invalid && (genero.dirty || genero.touched)"
              >
                Este campo es requerido.
              </div>
            </div>

            <div class="form-group col-6">
              <label for="fecha">Fecha de Estreno:</label>
              <input
                type="date"
                class="form-control"
                [(ngModel)]="currentItem.fecha"
                name="fecha"
                required
                #fecha="ngModel"
                [ngClass]="{
                  'is-invalid': fecha.invalid && (fecha.dirty || fecha.touched)
                }"
              />
              <div
                class="invalid-feedback"
                *ngIf="fecha.invalid && (fecha.dirty || fecha.touched)"
              >
                Este campo es requerido.
              </div>
            </div>
            <div class="form-group col-6">
              <label for="imagen">Url de la imagen:</label>
              <input
                class="form-control"
                [(ngModel)]="currentItem.imagen"
                name="imagen"
                required
                #imagen="ngModel"
                [ngClass]="{
                  'is-invalid':
                    imagen.invalid && (imagen.dirty || imagen.touched)
                }"
              />
              <div
                class="invalid-feedback"
                *ngIf="imagen.invalid && (imagen.dirty || imagen.touched)"
              >
                Este campo es requerido.
              </div>
            </div>
          </div>
          <div class="modal-footer">
            <button
              type="button"
              class="btn btn-secondary"
              (click)="closeModal(itemForm)"
            >
              Cancelar
            </button>
            <button type="submit" class="btn btn-info text-white col-6">
              {{ currentItem._id ? "Actualizar" : "Agregar" }}
            </button>
          </div>
        </form>
      </div>
    </div>
  </div>

  <hr />

  <input
    type="text"
    class="form-control mb-3"
    placeholder="Buscar películas"
    [(ngModel)]="searchTerm"
  />

  <table class="table">
    <thead>
      <tr>
        <th>Título</th>
        <th>Descripción</th>
        <th>Género</th>
        <th>Fecha de Estreno</th>
        <th>Imagen</th>
        <th>Acciones</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let item of filteredItems">
        <td>{{ item.name }}</td>
        <td>{{ item.description }}</td>
        <td>{{ item.genero }}</td>
        <td>{{ item.fecha | date }}</td>
        <td>
          <img
            *ngIf="item.imagen"
            [src]="item.imagen"
            alt="Imagen de la película"
            style="max-width: 100px; max-height: 100px"
          />
        </td>
        <td>
          <!-- Botones de acción con confirmación para eliminar -->
          <button
            class="btn btn-sm btn-primary"
            (click)="editItem(item._id); openModal()"
          >
            Editar
          </button>
          <button class="btn btn-sm btn-danger" (click)="deleteItem(item._id)">
            Eliminar
          </button>
        </td>
      </tr>
    </tbody>
  </table>
</div>
