import { Component, OnInit } from '@angular/core';
import { ItemService } from '../item.service';

@Component({
  selector: 'app-item',
  templateUrl: './item.component.html',
  styleUrls: ['./item.component.css'],
})
export class ItemComponent implements OnInit {
  items: any[] = [];
  currentItem: any = {};

  constructor(private itemService: ItemService) {}

  ngOnInit(): void {
    this.getItems();
  }

  getItems(): void {
    this.itemService.getItems().subscribe((items) => {
      this.items = items;
    });
  }

  getItemById(id: string): void {
    this.itemService.getItemById(id).subscribe((item) => {
      this.currentItem = item;
    });
  }

  createItem(item: any): void {
    this.itemService.createItem(item).subscribe(() => {
      this.getItems();
      this.currentItem = {};
    });
  }

  updateItem(id: string, item: any): void {
    this.itemService.updateItem(id, item).subscribe(() => {
      this.getItems();
      this.currentItem = {};
    });
  }

  deleteItem(id: string): void {
    this.itemService.deleteItem(id).subscribe(() => {
      this.getItems();
    });
  }

  editItem(id: string): void {
    this.getItemById(id);
  }
}
<div class="form-group">
  <label for="description">Descripción:</label>
  <textarea
    class="form-control"
    [(ngModel)]="currentItem.description"
    name="description"
    required
    #description="ngModel"
    [ngClass]="{
      'is-invalid':
        description.invalid && (description.dirty || description.touched)
    }"
  ></textarea>
  <div
    class="invalid-feedback"
    *ngIf="description.invalid && (description.dirty || description.touched)"
  >
    La descripción es obligatoria.
  </div>
</div>

<div class="form-group">
  <label for="genre">Género:</label>
  <input
    type="text"
    class="form-control"
    [(ngModel)]="currentItem.genre"
    name="genre"
    required
    #genre="ngModel"
    [ngClass]="{
      'is-invalid': genre.invalid && (genre.dirty || genre.touched)
    }"
  />
  <div
    class="invalid-feedback"
    *ngIf="genre.invalid && (genre.dirty || genre.touched)"
  >
    El género es obligatorio.
  </div>
</div>

<div class="form-group">
  <label for="director">Director:</label>
  <input
    type="text"
    class="form-control"
    [(ngModel)]="currentItem.director"
    name="director"
    required
    #director="ngModel"
    [ngClass]="{
      'is-invalid': director.invalid && (director.dirty || director.touched)
    }"
  />
  <div
    class="invalid-feedback"
    *ngIf="director.invalid && (director.dirty || director.touched)"
  >
    El director es obligatorio.
  </div>
</div>

<div class="form-group">
  <label for="releaseYear">Año de estreno:</label>
  <input
    type="number"
    class="form-control"
    [(ngModel)]="currentItem.releaseYear"
    name="releaseYear"
    required
    #releaseYear="ngModel"
    [ngClass]="{
      'is-invalid':
        releaseYear.invalid && (releaseYear.dirty || releaseYear.touched)
    }"
  />
  <div
    class="invalid-feedback"
    *ngIf="releaseYear.invalid && (releaseYear.dirty || releaseYear.touched)"
  >
    El año de estreno es obligatorio y debe ser un número.
  </div>
</div>

<!-- Agregar más campos según el enfoque del tema de películas -->

<button type="submit" class="btn btn-primary">
  {{ currentItem._id ? "Actualizar" : "Agregar" }}
</button>
<table class="table">
  <thead>
    <tr>
      <th>Título</th>
      <th>Descripción</th>
      <th>Género</th>
      <th>Director</th>
      <th>Año de estreno</th>
      <th>Acciones</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let item of items">
      <td>{{ item.title }}</td>
      <td>{{ item.description }}</td>
      <td>{{ item.genre }}</td>
      <td>{{ item.director }}</td>
      <td>{{ item.releaseYear }}</td>
      <td>
        <button class="btn btn-sm btn-primary" (click)="editItem(item._id)">
          Editar
        </button>
        <button class="btn btn-sm btn-danger" (click)="deleteItem(item._id)">
          Eliminar
        </button>
      </td>
    </tr>
    <tr>
      <td colspan="6" class="text-center">No se encontraron películas.</td>
    </tr>
  </tbody>
</table>
